const makeName = (x, y) => {
  return [x, y].sort().join('_');
}

const Query = {
  chatBox: async (parent, { name1 , name2 }, { ChatBoxModel }) => {
    const name = makeName(name1, name2);
    console.log(name);
    let box = await ChatBoxModel.findOne({ name });
    if (!box) {
      box = await new ChatBoxModel({ name }).save();
    }
    return box;
  }
}

export { Query as default };

